import socket
from multiprocessing import connection
from multiprocessing.reduction import _reduce_socket #type: ignore

from .reduction import register #type: ignore

# register reduction for win32 communication objects
register(socket.socket, _reduce_socket) #type: ignore
register(connection.Connection, connection.reduce_connection) #type: ignore
register(connection.PipeConnection, connection.reduce_pipe_connection) #type: ignore
